mapper.select:
select branch_name, course_name, course_code, class_code, class_name, status,teacher_name, start_date, end_date from (select /*+INDEX_DESC (CLASS PK_CLASS )*/ rownum rn, branch_name, course_name, course_code, class_code, class_name, status,teacher_name, start_date, end_date from (select co.branch_name, co.course_name, cl.course_code, cl.class_code,cl.class_name, cl.status,cl.teacher_name, start_date, end_date from class cl INNER JOIN course co ON cl.course_code = co.course_code where
cl.status ='Y' order by to_number(cl.class_code) desc)
WHERE rownum <= #{pageNum} * #{amount}
) where rn > (#{pageNum} -1) * #{amount}

mapper.update:
update class
set status = 'N'
where class_code = #{class_code}

mapper.select:
	select branch_name, course_name, class_code, branchcode, course_code, status, class_name, teacher_name, tuition, start_date, end_date, start_time, end_time, class_target, enter, updatedate  
		from (
        select co.branch_name, co.course_name, cl.class_code, cl.branchcode, cl.course_code,cl.status, cl.class_name, cl.teacher_name, cl.tuition, start_date, end_date, start_time, end_time, cl.class_target, cl.enter,cl.updatedate 
		from class cl
		inner join course co
		on cl.course_code = co.course_code
		)
		where class_code = #{class_code}

select class_code
from class
where class_code = #{class_code}
